mapper.select:
SELECT a.* FROM SYS_LOG a
WHERE a.CREATE_TIME BETWEEN #{beginDate} AND #{endDate}
AND a.TITLE LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.CREATE_ID = #{createId}
AND a.REQUEST_URI LIKE '%'||#{requestUri}||'%' '%'+#{requestUri}+'%' CONCAT('%', #{requestUri}, '%')
AND a.TYPE = #{TYPE_EXCEPTION}
${sqlMap.dsf} ORDER BY a.CREATE_TIME DESC

mapper.insert:
INSERT INTO SYS_LOG( ID, TYPE, TITLE, CREATE_ID, CREATE_TIME, REMOTE_ADDR, USER_AGENT, REQUEST_URI, METHOD, PARAMS, EXCEPTION,
APP_USER_ID, APP_ID, DEVICE_TYPE, DEVICE_MODEL, REMOTE_IP, SYSTEM_VERSION, MAC_ADDRESS, IMEI, RESP_TIME, LOCATION, NETTYPE, DEVOPERATORS, SESSION_ID, COOKIE_ID, EQUIPMENT_MODEL, APP_VERSION
) VALUES ( #{id}, #{type}, #{title}, #{createId}, #{createTime}, #{remoteAddr}, #{userAgent}, #{requestUri}, #{method}, #{params}, #{exception}, #{appUserId}, #{appId}, #{deviceType}, #{deviceModel}, #{remoteIp}, #{systemVersion}, #{macAddress}, #{imei}, #{respTime}, #{location}, #{nettype}, #{devoperators}, #{sessionId}, #{cookieId}, #{equipmentModel}, #{appVersion} )

